function btnMultiReqIssue_OnClick(obj)
{
	if(!fcMultiCheck()){
		return;
	}

	var sArg = "Arg_sUserId=" + quote(gdsUser.GetColumn(gdsUser.rowposition, "USER_ID"))
	         + " Arg_sUserNm=" + quote(gdsUser.GetColumn(gdsUser.rowposition, "USER_NM"))
	         + " Arg_sIssuYear=" + quote(edIssuYear.Text)
	         + " Arg_sIssuCnt=" + quote(edIssuCnt.Text)
	         + " Arg_sUsePurposes=" + quote(cboPurposes.Value);

	var sResponseCnt = Dialog("form::E03110012.xml", sArg, 834, 450, "");

	if ( sResponseCnt > 0 ) fcSave();
}
