//조회 
function fcSearch(obj) {

	var sServiceName = "e03110010-service:searchCertIssu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCertIssu=dsOutCertIssu  ";

	fce_AddParam(Trim("CERT_KND_CD"), "001"); //선수증명서
	fce_AddParam(Trim("ISSU_YEAR"), cboSIssuYear.Value);
	fce_AddParam(Trim("PURPOSES_CD"), cboSPurposes.Value);
	fce_AddParam(Trim("NM_KOR"), edRacerNm.Text);
    fce_AddParam(Trim("ISSU_DTM"), calIssuDt.Value);
    fce_AddParam(Trim("REQ_TYPE"), cboReqType.Value);		//IWORKS_SFR006 2013.12.21

 	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

