//IWORKS_SFR006 2013.12.21

function fcSave(obj) {
	//IWORKS_SFR006 2013.12.21
	for(var i = 0; i < dsOutCertIssu.rowcount; i++){
		if(toUpper(dsOutCertIssu.GetRowType(i)) == "UPDATE"){
			if(dsOutCertIssu.GetColumn(i, "PRS_STAT_CD") == "003" && fnc_IsNull(trim(dsOutCertIssu.GetColumn(i, "RJT_RS")))){
				fnc_Message(L_COM_CNF_0000, "N", "반려사유를 입력하세요. [" + (i + 1) + " 행 ]");
				dsOutCertIssu.rowpos = i;
				return false;
			}
		}
	}

    var ServiceName = "e03110010-service:SaveCertConfirm";
    var sInDataSet   = "dsOutCertIssu=dsOutCertIssu:u";
    var sOutDataSet  = "dsOutCertIssu=dsOutCertIssu";

    fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");  
}

