/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(ServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( ServiceName == "common-service:searchCode" ) {
        fce_AddCodeRow(dsOutPurposes, "전체", "", cboSPurposes);
        fcInit();

		return;
	}	

	// 조회 후
	if ( ServiceName == "e03110010-service:searchCertIssu" ) {
	    fnc_SetStatus(SNIS_BOA_0003,  dsOutCertIssu.GetRowCount());
	}

	// 저장 후
	if ( ServiceName == "e03110010-service:SaveCertConfirm" || ServiceName == "e03110010-service:SaveCertPrintCheck" ) {
		fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProceS(ServiceName);
}

