/*-------------------------------------------------------------------------------------------------+
>>  출주선수명단 클릭 시
+-------------------------------------------------------------------------------------------------*/
function btn_racerlist_OnClick(obj)
{
	http.sync = true;
    fcSearchRacerlist();
	http.sync = false;

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03129010_racer_list.jrf";               //레포트 파일명

	var sDataSet  = "racer_list=dsOutRacerList";   //"UBI 쿼리명, MIP Dataset명"

	var sArg  = " TMS="+cboSRaceTms.Value; 
		sArg += " DAY_ORD="+cboSRaceDayOrd.Value;
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

