/*-------------------------------------------------------------------------------------------------+
>>  출주선수명단-당일공지용 클릭 시
+-------------------------------------------------------------------------------------------------*/
function btn_racerlist_date_OnClick(obj)
{
 	http.sync = true;
    fcSearchRacerlist_Date();
	http.sync = false;

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03129040_racerlist_date.jrf";               //레포트 파일명
//	alert(dsOutRacerList_Date.GetColumn(0, "MAX_RACE_REG_NO"));
	if(dsOutRacerList_Date.GetColumn(0, "MAX_RACE_REG_NO") == 7){
		sRptNm    = "e03129040_racerlist_date7.jrf"; 
	}else if(dsOutRacerList_Date.GetColumn(0, "MAX_RACE_REG_NO") == 8){
		sRptNm    = "e03129040_racerlist_date8.jrf"; 
	}

	var sDataSet  = "racerlist_date=dsOutRacerList_Date";   //"UBI 쿼리명, MIP Dataset명"

	var sArg  = " TMS="+cboSRaceTms.Value;
        sArg += " DAY_ORD="+cboSRaceDayOrd.Value;
        sArg += " IMG_URL="+logImgUrl;
	var sRptDrct    = 1;     //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
}

