function btnArrangeListPrintOnClick(obj)
{
	fcCallRPTPrint( "e03120010_arrange_list");
}
function fcCallRPTPrint(rptFileName){
	var argsKeys = "STND_YEAR#MBR_CD#TMS";
	var argsValues = cboSStndYear.Value + "#" + "001" + "#" + cboSRaceTms.Value;
	fnc_SQLRPTPrint("e03120010", rptFileName, 0, argsKeys, argsValues);
}
