/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsOutRaceResult.ClearData();
	dsOutVoilCnt.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSRaceDayOrd)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSRaceDayOrd.SetFocus();
            return false;
    }

    return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    //경주결과조회 
    var sServiceName = "";
    var sInDataSet = ""; 
    var sOutDataSet = "";

	sServiceName = "e03130020-service:searchRaceResult";
	sInDataSet   = "";
	sOutDataSet  = "dsOutRaceResult=dsOutRaceResult "
				 + "dsOutVoilCnt=dsOutVoilCnt "
				 + "dsOutRacerEduData=dsOutRacerEduData ";

	dsOutRaceResult.ClearData();
	dsOutVoilCnt.ClearData();
	dsOutRacerEduData.ClearData();

	fce_AddParam(Trim("STND_YEAR"), 	cboSStndYear.Value	);
	fce_AddParam(Trim("MBR_CD   "),     CURR_MBR_CD         );
	fce_AddParam(Trim("TMS      "), 	cboSRaceTms.Value 	);
	fce_AddParam(Trim("DAY_ORD  "), 	cboSRaceDayOrd.Value);
    fce_AddParam(Trim("RACE_NO  "), 	cboSRaceNo.Value    );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerEduData) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerEduData;
	sMandatoryColID   = "STND_YEAR||MBR_CD||TMS||DAY_ORD|RACE_NO||CNTNT";
	sMandatoryColName = "기준년도||경정장코드||회차||일차||경주번호||특이사항";
	oGrid             = null;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

	// 특이사항 저장
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e03130020-service:saveRacerEduData";
    var sInDataSet   = "dsOutRacerEduData=dsOutRacerEduData:u ";
    var sOutDataSet  = "dsOutRacerEduData=dsOutRacerEduData ";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var grdName = "";
	grdName = "grdRaceResult";
	eval(grdName).ExportExcelEx("지정연습결과표");

	grdName = "grdVoilCnt";
	eval(grdName).ExportExcelEx("사고점");
}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03130020_racer_edu_data.jrf";               //레포트 파일명
	var sDataSet  = "race_result=dsOutRaceResult ";   //"UBI 쿼리명, MIP Dataset명"
	    sDataSet += "race_voil=dsOutVoilCnt ";
	    sDataSet += "racer_edu_data=dsOutRacerEduData ";

	var sArg  = " IMG_URL="+logImgUrl;
		sArg += " STND_YEAR="+cboSStndYear.Value;
		sArg += " TMS="+cboSRaceTms.Value;
		sArg += " DAY_ORD="+cboSRaceDayOrd.Value;
		sArg += " RACE_NO="+cboSRaceNo.Value;

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
}

