/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fce_GetRaceTms(cboSStndYear.Value,CURR_MBR_CD,"","dsOutSRaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
		dsOutSMaxTms.Filter("STND_YEAR='"+cboSStndYear.Value+"'&&MBR_CD='001'");
		var maxTms = dsOutSMaxTms.GetColumn(0,"MAX_TMS");
		dsOutSRaceTms.Filter("TMS<=" + maxTms);

        cboSRaceTms.Index = 0;

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }
        cboSRaceTms_OnChanged();
		return;
	}

	// 일차 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSRaceDayOrd.Index = 0;

        if ( cboSRaceTms.Value == CURR_TMS ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", CURR_DAY_ORD);

            if ( nIndexRow > -1 ) cboSRaceDayOrd.Index = nIndexRow;
        }

        fce_GetRace(cboSStndYear.Value, CURR_MBR_CD, cboSRaceTms.Value, cboSRaceDayOrd.Value, "dsOutSRaceNo");
		return;
	}

	// 경주 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRace" ) {
        cboSRaceNo.Index = 0;
        /*if ( cboSRaceDayOrd.Value == CURR_DAY_ORD ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceNo, "RACE_NO", CURR_RACE_NO );

            if ( nIndexRow > -1 ) cboSRaceNo.Index = nIndexRow;
        }*/

        //fcSearch();
		return;
	}


	//조회후
	if ( sServiceName == "e03130020-service:searchRaceResult" ) { // 경주성적(회차별)

		if(dsOutRacerEduData.rowcount == 0)
		{
			dsOutRacerEduData.AddRow();
			dsOutRacerEduData.SetColumn(0,"STND_YEAR", cboSStndYear.Value);
			dsOutRacerEduData.SetColumn(0,"MBR_CD", CURR_MBR_CD);
			dsOutRacerEduData.SetColumn(0,"TMS", cboSRaceTms.Value);
			dsOutRacerEduData.SetColumn(0,"DAY_ORD", cboSRaceDayOrd.Value);
			dsOutRacerEduData.SetColumn(0,"RACE_NO", cboSRaceNo.Value);
		}
        fnc_SetStatus(SNIS_BOA_0003, dsOutRaceResultPivot.GetRowCount() + dsOutVoilCntPivot.GetRowCount());
        //fcGrdDisplay("grdRaceResult");
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

