*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
var MAX_RACE_REG_NO_1 = -1;
var MAX_RACE_REG_NO_3 = -1;

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

		//기수전체추가
		dsOutPerioNo.InsertRow(0); 
		dsOutPerioNo.SetColumn(0,"RACER_PERIO_NO", ""); 
		cboRacerPerioNo.Index = 0;
		//등급전체추가 
		dsOutRacerGrd.InsertRow(0); 
		dsOutRacerGrd.SetColumn(0, "CD_NM", ""); 
		cboRacerGrd.Index = 0;
		//등급전체추가 
		cboRacerSex.Index = 0;
		return;
	}

	// 평균스타트타임 조회시 
	if ( sServiceName == "e04110020-service:searchStatTime1" ) {

        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime1.GetRowCount());

        setGridSize1(MAX_RACE_REG_NO_1);

        return;
	}

	// 스타트타임 초별 분포율 조회시 
	if ( sServiceName == "e04110020-service:searchStatTime2" ) {


        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime2.GetRowCount());
         return;
	}

    // 진입코스별 스타트타임 현황 조회시 
	if ( sServiceName == "e04110020-service:searchStatTime3" ) {


        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime3.GetRowCount());

        setGridSize3(MAX_RACE_REG_NO_3);
         return;
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var scboSearchValue = cboSearch.value ; 

	if (scboSearchValue == '1') {			// 평균 스타트타임	
		fnc_OpenExcelExport("grdStatTime1");      
	}else if (scboSearchValue == '2') {		// 스타트타임 초별 분포율	
		fnc_OpenExcelExport("grdStatTime2");      
	}else if (scboSearchValue == '3') {		// 진입코스별 스타트타임 현황
		fnc_OpenExcelExport("grdStatTime3");      
	}

}

