/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsOutStatTime1.ClearData();
	dsOutStatTime2.ClearData();
	dsOutStatTime3.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}


/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/

function cboSearch_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var scboSearchValue = cboSearch.value ; 

	if (scboSearchValue == '1') {			// 평균 스타트타임
		stcTitle.text = "평균 스타트타임" ; 
		grdStatTime1.Visible = true ; 
		grdStatTime2.Visible = false ; 
		grdStatTime3.Visible = false ; 

	}else if (scboSearchValue == '2') {		// 스타트타임 초별 분포율
		stcTitle.text = "스타트타임 초별 분포율" ; 
		grdStatTime1.Visible = false ; 
		grdStatTime2.Visible = true ; 
		grdStatTime3.Visible = false ; 
	}else if (scboSearchValue == '3') {		// 진입코스별 스타트타임 현황
		stcTitle.text = "진입코스별 스타트타임 현황" ; 
		grdStatTime1.Visible = false ; 
		grdStatTime2.Visible = false ; 
		grdStatTime3.Visible = true ; 
	}
	fcSearch(); 
}

function cboSMbr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch(); 
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 

    if(fnc_isNull(calRaceFromDt.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경주시작일자");
            calRaceFromDt.SetFocus();
            return false;
    } 

    if(fnc_isNull(calRaceToDt.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경주종료일자");
            calRaceToDt.SetFocus();
            return false;
    } 

    if(calRaceFromDt.Value > calRaceToDt.Value) {
            fnc_Message(SNIS_BOA_E001, "N");
            calRaceFromDt.SetFocus();
            return false;
    } 

    if( Trim(cboSearch.Value) == "1") {			  

	} 
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";

    if( Trim(cboSearch.Value) == "1") {							//평균 스타트타임   
	    sServiceName = "e04110020-service:searchStatTime1";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime1=dsOutStatTime1";

	} else if (Trim(cboSearch.Value) == "2") {					//스타트타임 초별 분포율
	    sServiceName = "e04110020-service:searchStatTime2";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime2=dsOutStatTime2";

	} else if (Trim(cboSearch.Value) == "3") {					//진입코스별 스타트타임 현황
		sServiceName = "e04110020-service:searchStatTime3";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime3=dsOutStatTime3";
   } 

    fce_AddParam(Trim("STND_YEAR"), 			cboSStndYear.Value		);
	fce_AddParam(Trim("MBR_CD"), 				cboSMbr.Value     		);
	fce_AddParam(Trim("FROMDT"), 				calRaceFromDt.Value 	);
	fce_AddParam(Trim("TODT"), 					calRaceToDt.Value 		);
	fce_AddParam(Trim("RACER_PERIO_NO"), 		cboRacerPerioNo.Value 	); //기수
	fce_AddParam(Trim("RACER_GRD_CD"), 			cboRacerGrd.Value 		); //등급
    fce_AddParam(Trim("RES_NO"), 				cboRacerSex.Value 		); //성별

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}

