/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setGridSize1(nMaxRaceRegNoCnt){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;

	//Alert("111 nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	if(nMaxRaceRegNoCnt == 0 ) nMaxRaceRegNoCnt = 6;

	grdStatTime1.Redraw = false;

	var nCol0W = grdStatTime1.GetColProp(0, "Width");
	var nCol1W = grdStatTime1.GetColProp(1, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        grdStatTime1.SetColProp(2 + i, "Width", 80);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        grdStatTime1.SetColProp(2 + i-1, "Width", 0);
    }

    grdStatTime1.SetColProp(0, "Width", nCol0W);
    grdStatTime1.SetColProp(1, "Width", nCol1W);

    grdStatTime1.Redraw = true;
}

/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setGridSize3(nMaxRaceRegNoCnt){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;

	//Alert("222 nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	if(nMaxRaceRegNoCnt == 0 ) nMaxRaceRegNoCnt = 6;

	grdStatTime3.Redraw = false;

	var nCol0W = grdStatTime3.GetColProp(0, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        grdStatTime3.SetColProp(1 + i, "Width", 90);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        grdStatTime3.SetColProp(1 + i-1, "Width", 0);
    }

    grdStatTime3.SetColProp(0, "Width", nCol0W);

    grdStatTime3.Redraw = true;
}
