function fcInit(obj) {
   dsOutRace.ClearData(); 
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms1.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "시작회차");
            cboSRaceTms1.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSRaceTms2.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "종료회차");
            cboSRaceTms2.SetFocus();
            return false;
    }

    MIN_TMS = cboSRaceTms1.Value;
    MAX_TMS = cboSRaceTms2.Value;  
    fcSearchStartTime(CURR_STNT_YEAR, CURR_USER_MBR_CD, MIN_TMS, MAX_TMS);

   	return true;

}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

}


