function fcSearchStartTime(stndYear, mbrCd, minTms, maxTms) {

	if(MIN_TMS == null){
			alert("등록된 회차가 없습니다. ");
			return;
	}

	var ServiceName  = 'e04110030-service:searchRace';
    var sInDataSet   = '';
    var sOutDataSet  = 'dsOutRace=dsOutRace';
    dsOutRace.ClearData();

    fce_AddParam(Trim("STND_YEAR"), 	cboSStndYear.Value	);
	fce_AddParam(Trim("MBR_CD   "), 	cboSMbr.Value     	);
	fce_AddParam(Trim("TMS_FROM "), 	calRaceDt1.Value 	);
	fce_AddParam(Trim("TMS_TO   "), 	calRaceDt2.Value    );
	fce_AddParam('MIN_TMS', minTms);
	fce_AddParam('MAX_TMS', maxTms);	

    fce_AddParam(Trim("SEARCH_COND  "), cboSearch.Value   	);
    //fce_AddParam(Trim("SEARCH_VALUE "), edtSearch.text   	);

	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, 'fcCallBack', false);
}

//Grit Format생성 
function setDayHeader(minTms, maxTms){
	var gridFormat = '';
	gridFormat += " <contents> ";
	gridFormat += " 	<format id='Default'> ";
	gridFormat += " 		<columns> ";
	if        ( Trim(cboSearch.Value) == "1") {
		gridFormat += " 			<col fix='left' width='58'/> "; //선수번호
		gridFormat += " 			<col fix='left' width='33'/> "; //기수
		gridFormat += " 			<col fix='left' width='30'/> "; //등급
	} else if ( Trim(cboSearch.Value) == "2") {  
		gridFormat += " 			<col fix='left' width='30'/> "; //등급
		gridFormat += " 			<col fix='left' width='58'/> "; //선수번호
		gridFormat += " 			<col fix='left' width='33'/> "; //기수
	} else if ( Trim(cboSearch.Value) == "3") { 
		gridFormat += " 			<col fix='left' width='33'/> "; //기수
		gridFormat += " 			<col fix='left' width='58'/> "; //선수번호
		gridFormat += " 			<col fix='left' width='30'/> "; //등급
	}
	gridFormat += " 			<col fix='left' width='57'/> ";
	gridFormat += " 			<col fix='left' width='57'/> ";
	gridFormat += " 			<col fix='left' width='54'/> ";
	gridFormat += " 			<col fix='left' width='65'/> ";
	gridFormat += " 			<col fix='left' width='62'/> ";
	gridFormat += " 			<col fix='left' width='45'/> ";
	gridFormat += " 			<col fix='left' width='50'/> ";

	for(var i=minTms; i<maxTms+1; i++){
			gridFormat += " 			<col width='50'/> ";
	}
	gridFormat += " 		</columns> ";
	gridFormat += " 		<head> ";
	if        ( Trim(cboSearch.Value) == "1") {
		gridFormat += " 			<cell align='center' bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' rowspan='2' text='선수번호'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' rowspan='2' text='기수'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' rowspan='2' text='등급'/> ";
	} else if ( Trim(cboSearch.Value) == "2") {  
		gridFormat += " 			<cell align='center' bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' rowspan='2' text='등급'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' rowspan='2' text='선수번호'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' rowspan='2' text='기수'/> ";
	} else if ( Trim(cboSearch.Value) == "3") {  
		gridFormat += " 			<cell align='center' bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' rowspan='2' text='기수'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' rowspan='2' text='선수번호'/> ";
		gridFormat += " 			<cell align='center' bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' rowspan='2' text='등급'/> ";
	}
	gridFormat += " 			<cell align='center' bkcolor='user20' col='3' color='user21' display='text' font='굴림,9' rowspan='2' text='선수명'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='4' color='user21' display='text' font='굴림,9' rowspan='2' text='출주횟수'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='5' color='user21' display='text' font='굴림,9' rowspan='2' text='평균ST'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='6' color='user21' display='text' font='굴림,9' rowspan='2' text='최고타임'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='7' color='user21' display='text' font='굴림,9' rowspan='2' text='최저타임'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='8' color='user21' display='text' font='굴림,9' rowspan='2' text='F'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='9' color='user21' display='text' font='굴림,9' rowspan='2' text='L'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='10' color='user21' colspan='"+(maxTms-minTms+1)+"' display='text' font='굴림,9' text='회차별 스타트타임'/> ";
	for(var i=1; i<=(maxTms-minTms+1); i++){
		gridFormat += " 		<cell align='center' bkcolor='user20' col='"+(i + 10 -1)+"' color='user21' display='text' font='굴림,9' row='1' text='"+(i+minTms-1)+"&#32;회차'/> ";
	}
	gridFormat += " 		</head> ";

	gridFormat += " 		<body> ";
	if        ( Trim(cboSearch.Value) == "1") { //선수별
		gridFormat += " 			<cell col='0' align='center' colid='RACER_NO' display='text' suppress='1'/> ";
		gridFormat += " 			<cell col='1' align='center' colid='RACER_PERIO_NO' display='text' suppress='2'/> ";
		gridFormat += " 			<cell col='2' align='center' colid='RACER_GRD_CD' display='text' suppress='3'/> ";
	} else if ( Trim(cboSearch.Value) == "2") { //등급별
		gridFormat += " 			<cell col='0' align='center' colid='RACER_GRD_CD' display='text' suppress='1'/> ";
		gridFormat += " 			<cell col='1' align='center' colid='RACER_NO' display='text' suppress='2'/> ";
		gridFormat += " 			<cell col='2' align='center' colid='RACER_PERIO_NO' display='text' suppress='3'/> ";
	} else if ( Trim(cboSearch.Value) == "3") { //기수별
		gridFormat += " 			<cell col='0' align='center' colid='RACER_PERIO_NO' display='text' suppress='1'/> ";
		gridFormat += " 			<cell col='1' align='center' colid='RACER_NO' display='text' suppress='2'/> ";
		gridFormat += " 			<cell col='2' align='center' colid='RACER_GRD_CD' display='text' suppress='3'/> ";
	}
	gridFormat += " 			<cell col='3' align='center' colid='RACER_NM' display='text' suppress='4'/>";
	gridFormat += " 			<cell col='4' align='center' colid='RACE_CNT' display='text' suppress='5'/>";
	gridFormat += " 			<cell col='5' align='center' colid='AVG_STAR_TM' display='text' suppress='6'/>";
	gridFormat += " 			<cell col='6' align='center' colid='MAX_STAR_TM' display='text' suppress='7'/>";
	gridFormat += " 			<cell col='7' align='center' colid='MIN_STAR_TM' display='text' suppress='8'/>";
	gridFormat += " 			<cell col='8' align='center' colid='FLYING_CNT' display='text' suppress='9'/>";
	gridFormat += " 			<cell col='9' align='center' colid='LATE_CNT' display='text' suppress='10'/>";

	var bodyColumnIndex =9;
	for (var i=minTms;i<=maxTms;i++) {
		gridFormat += " 		<cell col='"+(++bodyColumnIndex)+"' align='center' colid='TMS_"+i+"' display='number' edit='normal'/> ";
	}
	gridFormat += " 		</body> ";
	gridFormat += " 	</format> ";
	gridFormat += " </contents> ";

	grdRace.contents = gridFormat;

} 


function dsOutRace_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	setDayHeader(ToNumber(MIN_TMS), ToNumber(MAX_TMS));
}
