/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutStatTime1.ClearData();
	dsOutStatTime2.ClearData(); 
    dsOutStatTime3.ClearData();
	dsOutStatTime4.ClearData(); 
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	var sDt  = cboSStndYear.Value;
	var sTms = cboSRaceTms.Value;
	var eTms = cboERaceTms.Value;

	if(    fnc_isNull(sDt) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작년도가 ");
        cboSStndYear.Value = "";
        return false;
	}	

    fcInit(obj);
 	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	if ( !fcSearchCheck(obj) ) return;

    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";

    if( Trim(cboSearch.Value) == "1") {							//평균 경주 주회타임
	    sServiceName = "e04140010-service:searchStatTime1";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime1=dsOutStatTime1";

	} else if (Trim(cboSearch.Value) == "2") {					//월별 선두정 주회타임 비교
	    sServiceName = "e04140010-service:searchStatTime2";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime2=dsOutStatTime2";

	} else if (Trim(cboSearch.Value) == "3") {					//년도별 BEST10 주회타임
	    sServiceName = "e04140010-service:searchStatTime3";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime3=dsOutStatTime3";
	} else if (Trim(cboSearch.Value) == "4") {
	    sServiceName = "e04140010-service:searchStatTime4";
        sInDataSet   = "";
        sOutDataSet  = "dsOutStatTime4=dsOutStatTime4";
	}

    fce_AddParam("STR_DT"    , calStrDt.Value);
	fce_AddParam("END_DT"    , calEndDt.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

  }


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var scboSearchValue = cboSearch.value ; 

	if (scboSearchValue == '1') {			//평균 경주 주회타임
		fnc_OpenExcelExport("grdStatTime1");      
	}else if (scboSearchValue == '2') {		//월별 선두정 주회타임 비교
		fnc_OpenExcelExport("grdStatTime2");      
	} else if (scboSearchValue == '3') {	//년도별 BEST10 주회타임
		fnc_OpenExcelExport("grdStatTime3"); 
    } else if (scboSearchValue == '4') {
		fnc_OpenExcelExport("grdStatTime4"); 
    }
}

