/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboEMbr.Index = 0;

        fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
        fce_GetRaceTms(cboEStndYear.Value,cboEMbr.Value,"","dsOutERaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        if ( cboERaceTms.Index < 0 ) {
            cboERaceTms.Index = 0;
        }

        if ( gF_TmsGubun == "S" ) {
            cboSRaceTms.Index = dsOutSRaceTms.GetRowCount() - 1;
        }

        if ( gF_TmsGubun == "E" ) {
            cboERaceTms.Index = 0;

            if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
                var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
                if ( nIndexRow > -1 ) cboERaceTms.Index = nIndexRow;
            }
        }
        //fcSearchRaceDayOrd();

        cboSRaceTms_OnChanged();
        cboERaceTms_OnChanged();
		return;
	}
	//평균 경주 주회타임
	if ( sServiceName == "e04140010-service:searchStatTime1" ) {

        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime1.GetRowCount());
        Trace(dsOutStatTime1.savexml());
         return;
	}

	//월별 선두정 주회타임 비교
	if ( sServiceName == "e04140010-service:searchStatTime2" ) {


        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime2.GetRowCount());
         return;
	}

	//년도별 BEST10 주회타임
	if ( sServiceName == "e04140010-service:searchStatTime3" ) {


        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime3.GetRowCount());
         return;
	}

	//시간차가 < 0.00.099
	if ( sServiceName == "e04140010-service:searchStatTime4" ) {


        fnc_SetStatus(SNIS_BOA_0003, dsOutStatTime4.GetRowCount());
         return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

