function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit(obj);
    fce_GetRaceTms(cboSStndYear.value, cboSMbr.Value, "", "dsOutSRaceTms");
    gF_TmsGubun = "S";
}

function cboEStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit(obj);
    fce_GetRaceTms(cboEStndYear.value, cboEMbr.Value, "", "dsOutERaceTms");
    gF_TmsGubun = "E";
}

function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    calStrDt.Value = dsOutSRaceTms.GetColumn(nIndexRow, "STR_DT");
}

function cboERaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutERaceTms, "TMS", cboERaceTms.Value);
    calEndDt.Value = dsOutERaceTms.GetColumn(nIndexRow, "END_DT");
}


function cboSearch_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var scboSearchValue = cboSearch.value ; 

	if (scboSearchValue == '1') {			
		stcTitle.text = "평균 경주 주회타임" ; 
		grdStatTime1.Visible = true ; 
		grdStatTime2.Visible = false ;
		grdStatTime3.Visible = false ; 
		grdStatTime4.Visible = false ; 
	}else if (scboSearchValue == '2') {
		stcTitle.text = "월별 선두정 주회타임 비교" ; 
		grdStatTime1.Visible = false ; 
		grdStatTime2.Visible = true ; 
		grdStatTime3.Visible = false ; 
		grdStatTime4.Visible = false ; 
	} else if (scboSearchValue == '3') {
		stcTitle.text = "년도별 BEST10 주회타임" ; 
		grdStatTime1.Visible = false ; 
		grdStatTime2.Visible = false ; 
		grdStatTime3.Visible = true ; 
		grdStatTime4.Visible = false ; 
	} else if (scboSearchValue == '4') {
		stcTitle.text = "시간차가 < 0.00.099" ; 
		grdStatTime1.Visible = false ; 
		grdStatTime2.Visible = false ; 
		grdStatTime3.Visible = false ; 
		grdStatTime4.Visible = true ; 
	}
	fcSearch(); 
}
