/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	switch (tab0.TabIndex) {
		case 0:
			fcSearchFLTms(obj);
			break;
		case 1:
			fcSearchFLYear(obj);
			break;
		case 2:
			fcSearchFLRacer(obj);
			break;
		default:
			;
	}
}

function fcSearchCheckTms() {
   if (!fnc_IsMandatory(tab0.tab1.cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(tab0.tab1.cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(tab0.tab1.cboTms, "회차")) return false;
}

//조회 
function fcSearchFLTms(obj) {
	if(fcSearchCheckTms() == false) return;

    var sServiceName = "e04150010-service:searchFL";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutFL=dsOutFL";

    fce_AddParam("STND_YEAR", tab0.tab1.cboStndYear.Value);
    fce_AddParam("MBR_CD"   , tab0.tab1.cboMbrCd.Value);
    fce_AddParam("TMS"      , tab0.tab1.cboTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//조회 
function fcSearchFLYear(obj) {

    var sServiceName = "e04150010-service:searchFLYear";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutFLYear=dsOutFLYear";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//조회 
function fcSearchFLRacer(obj) {

    var sServiceName = "e04150010-service:searchFLRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutFLRacer=dsOutFLRacer dsOutFLRacerDetail=dsOutFLRacerDetail";

    //fce_AddParam("STND_YEAR", tab0.tab3.cboStndYear.Value);
    //fce_AddParam("MBR_CD"   , tab0.tab3.cboMbrCd.Value);
    //fce_AddParam("TMS"      , tab0.tab3.cboTms.Value);
    fce_AddParam("RACER_NM" , tab0.tab3.edRacerNm.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function fcSearchRacerFL(obj,nOldRow,nRow) {

    var sServiceName = "e04150010-service:searchRacerFL";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerFL=dsOutRacerFL ";

    fce_AddParam("RACER_NO", obj.GetColumn(nRow, "RACER_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

