/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "";
	switch (tab0.TabIndex) {
		case 0:
			sExcelList  = "tab0.tab1.grdFL:2";
			sExcelList += "|tab0.tab1.grdRacerFL:1"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격

			fnc_OpenMultiExcelExport(sExcelList,"회차별 출발위반 통계");
			break;
		case 1:
			fnc_OpenExcelExport("tab0.tab2.grdFLYear");
			break;
		case 2:
			sExcelList  = "tab0.tab3.grdFLRacer:2";
			sExcelList += "|tab0.tab3.grdFLRacerDetail:1"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격

			fnc_OpenMultiExcelExport(sExcelList,"선수별 출발위반 통계"+iif(tab0.tab3.edRacerNm.text<>"","("+tab0.tab3.edRacerNm.text+")",""));

			break;
	}
}

