/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e04150010-service:searchFL" ) {
	    if(dsOutFL.GetRowCount() > 0)	tab0.tab1.stMainCnt.Text = "( " + dsOutFL.GetRowCount() + " )건";
	    else 							tab0.tab1.stDetailCnt.Text = "";

	    return;
	} else if ( sServiceName == "e04150010-service:searchRacerFL" ) {
	    if(dsOutRacerFL.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutRacerFL.GetRowCount());
			tab0.tab1.stDetailCnt.Text = "( " + dsOutRacerFL.GetRowCount() + " )건";
	    }
	    else tab0.tab1.stDetailCnt.Text = "";

	    return;
	} else if ( sServiceName == "e04150010-service:searchFLRacer" ) {
	    if (tab0.tab3.chkRacerOnly.Value == true) {
			dsOutFLRacerDetail.filter("RACER_NO='" + dsOutFLRacer.GetColumn(dsOutFLRacer.row, "RACER_NO") + "'");
		}
		return;
	}



	// 회차 목록 생성후
	if( sServiceName == "common_race-service:searchRaceTms") {
		tab0.tab1.cboTms.Index = 0;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

