//row 변경시 
function dsOutFL_OnRowPosChanged(obj,nOldRow,nRow)
{
	//LATEST_ROW_POS = nOldRow;
	if (nRow < 0) return;
	if(nRow >= 0) {
		dsOutRacerFL.ClearData();
		fcSearchRacerFL(obj,nOldRow,nRow);
	}
}

//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutFL_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutFL_OnRowPosChanged();
}

//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(tab0.tab1.cboStndYear.Value, tab0.tab1.cboMbrCd.Value, null, "dsOutTms");
}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(tab0.tab1.cboStndYear.Value, tab0.tab1.cboMbrCd.Value, null, "dsOutTms");
}

//경정장 DataSet Load 완료 시
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	tab0.tab1.cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(tab0.tab1.cboStndYear.Value, tab0.tab1.cboMbrCd.Value, null, "dsOutTms");
}

function fcOnClick(obj,strValue)
{
	if (obj.id == "chkRacerOnly") {
		if (tab0.tab3.chkRacerOnly.Value == true) {
			dsOutFLRacerDetail.filter("RACER_NO='" + dsOutFLRacer.GetColumn(dsOutFLRacer.row, "RACER_NO") + "'");
		} else {
			dsOutFLRacerDetail.UnFilter();
		}
	}
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsOutFLRacer") {
		if (tab0.tab3.chkRacerOnly.Value == true) {
			dsOutFLRacerDetail.filter("RACER_NO='" + dsOutFLRacer.GetColumn(nRow, "RACER_NO") + "'");
		}
	}

}
