//조회 
function fcSearch(obj) {
	fcSearchCheck();

    var sServiceName = "e05040070-service:searchInvRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutInvRacer=dsOutInvRacer";

    fce_AddParam("STND_YEAR", cboStndYear.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutInvRacer))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutInvRacer;
	sMandatoryColID   = "RACER_NO|SANC_BASIS_CD|SMRY|SANC_RSN_CD|SANC_RSN|ACT_DT|HOMEPAGE_YN";
	sMandatoryColName = "등록번호|양정코드|관련근거|사유|사유내역|사유발생일|홈페이지표출";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	if ( calSTR_DT.Value > calEND_DT.Value)
	{
		fnc_Message(SNIS_BOA_E001, "N");
		return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05040070-service:saveInvRacer";
    var sInDataSet   = "dsOutInvRacer=dsOutInvRacer:u";
    var sOutDataSet  = "dsOutInvRacer=dsOutInvRacer";

	fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

