/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05040070-service:searchInvRacer" ) {
	    if(dsOutInvRacer.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutInvRacer.GetRowCount());
	    }
	}

	// 저장 후
	if ( sServiceName == "e05040070-service:saveInvRacer" ) {
		if(dsOutInvRacer.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutInvRacer.GetRowCount());
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchRacer" :
		case "common_help-service:searchSancBasis" :
			//코드명 조회 후 처리
			fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
	//변화가 없으면 굳이 실행 하지 않는다.
	if (length(obj.userdata) > 0 && obj.value == obj.userdata ) return;

	var sInputValue  = trim(obj.value);      //검색 할 자료 

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = object(obj.id);
	sGRDTIT  = "";

	switch(obj.id) {
		case "edtRACER_NO" :                   				//선수 등록번호
			vREVOBJ[0]   = "edtRACER_NO";        			//선수 등록번호를 받을 Text Object명
			vREVOBJ[1]   = "edtNM_KOR";        				//성명을 받을 Text Object명
			vREVOBJ[2]   = "edtRACER_GRD_CD";       		//등급을 받을 Text Object명
			sGRDTIT      = "등록번호:성명:등급";			//그리드 제목

			sServiceName = "common_help-service:searchRacer";
			sInDataSet   = "";                      		//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutRacer";			//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("RACER_NO"), Trim(edtRACER_NO.Text));
			break;
		case "edtSANC_BASIS_CD" :							//양정 코드 
			vREVOBJ[0]   = "edtSANC_BASIS_CD";				//부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edtSB_SMRY";    				//관련 근거를 받을 Text Object명
			sGRDTIT      = "양정코드:관련근거";				//그리드 제목

			sServiceName = "common_help-service:searchSancBasis";
			sInDataSet   = "";								//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutSancBasis";	//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("SANC_BASIS_CD"), Trim(edtSANC_BASIS_CD.Text));
			break;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//row 변경시 
function dsOutInvRacer_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (dsOutInvRacer.GetRowType(nRow) == "insert")
		edtRACER_NO.Enable = true;
	else
		edtRACER_NO.Enable = false;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutInvRacer_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutInvRacer.Row = LATEST_ROW_POS;
}
