function fcSearchCheck() {

}

//조회 
function fcSearch(obj) {
	fcSearchCheck();

    var sServiceName = "e05040090-service:searchIntroRunTime";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutIntroRunTime=dsOutIntroRunTime";

    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("FROM_YEAR", cboStndYear.Value);
    fce_AddParam("TO_YEAR", cboToStndYear.Value);
    fce_AddParam("FROM_TMS", cboFromTms.Value);
    fce_AddParam("TO_TMS", cboToTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//상세조회 
function fcSearch_Detail(sMotNo) {

    var sServiceName = "e05040090-service:searchIntroRunTime_Detail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutIntroRunTime_Detail=dsOutIntroRunTime_Detail";

    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("FROM_YEAR", cboStndYear.Value);
    fce_AddParam("TO_YEAR", cboToStndYear.Value);
    fce_AddParam("FROM_TMS", cboFromTms.Value);
    fce_AddParam("TO_TMS", cboToTms.Value);
    fce_AddParam("MOT_NO", sMotNo);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
