/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05040090-service:searchIntroRunTime" ) {
	    if(dsOutIntroRunTime.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutIntroRunTime.GetRowCount());
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

//row 변경시 
function dsOutIntroRunTime_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	//상세조회
	fcSearch_Detail(obj.GetColumn(nRow, "MOT_NO"));
}

//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutIntroRunTime_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	dsOutIntroRunTime.Row = LATEST_ROW_POS;
	dsOutIntroRunTime_OnRowPosChanged(obj, LATEST_ROW_POS, LATEST_ROW_POS);
}

//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.ID)
	{
		case "cboStndYear":
			fce_GetRaceTms(strCode, cboMbrCd.Value, null, "dsOutTms");
			break;
		case "cboToStndYear":
			fce_GetRaceTms(strCode, cboMbrCd.Value, null, "dsOutToTms");
			break;
	}
	fcSearch();
}
//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, strCode, null, "dsOutTms");
	fce_GetRaceTms(cboToStndYear.Value, strCode, null, "dsOutToTms");
	fcSearch();
}

//시작회차 변경시
function cboFromTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//마지막회차 변경시
function cboToTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//경정장 DataSet Load 완료 시
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
	fce_GetRaceTms(cboToStndYear.Value, cboMbrCd.Value, null, "dsOutToTms");
}
