function fcSearchCheck() {
	return true;
}

//조회 
function fcSearch(obj) {
	//변경된 자료가 있는지 체크
	if ( fnc_DatasetChangeCheck(dsOutRaceAnalysis) ) {
		//저장 여부 확인 
		if ( !fnc_Message(SNIS_BOA_E006, "Y") ) {
			return false;    //새로 조회하지 않는다.
		}		
	}

	fcSearchCheck();

    var sServiceName = "e05070020-service:searchRaceAnalysis";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRaceAnalysis=dsOutRaceAnalysis ";

    fce_AddParam("STND_YEAR", cboSStndYear.Value);
    fce_AddParam("TMS",       cboSRaceTms.Value);
    fce_AddParam("DAY_ORD",   cboSDayOrd.Value);
    fce_AddParam("RACE_NO",   cboSRaceNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//상세조회 
function fcSearchRaceResult(obj,nOldRow,nRow) {

	var sServiceName = "e05070020-service:searchRaceResult";
	var sInDataSet   = "";
	var sOutDataSet  = "dsOutRaceResult=dsOutRaceResult ";

    fce_AddParam("STND_YEAR", obj.GetColumn(nRow, "STND_YEAR"));
    fce_AddParam("TMS",       obj.GetColumn(nRow, "TMS"));
    fce_AddParam("DAY_ORD",   obj.GetColumn(nRow, "DAY_ORD"));
    fce_AddParam("RACE_NO",   obj.GetColumn(nRow, "RACE_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRaceAnalysis))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	var rtn_arr;
	var sTemp;
	var asFile;
    for ( i = 0; i < dsOutRaceAnalysis.GetRowCount(); i++ ) {
        if ( dsOutRaceAnalysis.GetColumn(i, "IMG_1_UPDATE") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edPhotoNm_1.Text, 
														 dsOutRaceAnalysis.GetColumn(i, "IMG_1"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일1 업로드 에러발생");
				return false;
			}
			//alert("파일1 =" + rtn_arr[3]);
			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRaceAnalysis.SetColumn(i, "IMG_1", asFile[asFile.length - 1]);
        }
        if ( dsOutRaceAnalysis.GetColumn(i, "IMG_2_UPDATE") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edPhotoNm_2.Text, 
														 dsOutRaceAnalysis.GetColumn(i, "IMG_2"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일2 업로드 에러발생");
				return false;
			}
			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRaceAnalysis.SetColumn(i, "IMG_2", asFile[asFile.length - 1]);
        }
        if ( dsOutRaceAnalysis.GetColumn(i, "IMG_3_UPDATE") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edPhotoNm_3.Text, 
														 dsOutRaceAnalysis.GetColumn(i, "IMG_3"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일3 업로드 에러발생");
				return false;
			}
			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRaceAnalysis.SetColumn(i, "IMG_3", asFile[asFile.length - 1]);
        }
        if ( dsOutRaceAnalysis.GetColumn(i, "IMG_4_UPDATE") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edPhotoNm_4.Text, 
														 dsOutRaceAnalysis.GetColumn(i, "IMG_4"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일4 업로드 에러발생");
				return false;
			}
			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRaceAnalysis.SetColumn(i, "IMG_4", asFile[asFile.length - 1]);
        }
    }

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;

	var sServiceName = "e05070020-service:saveRaceAnalysis";
    var sInDataSet   = "dsOutRaceAnalysis=dsOutRaceAnalysis:u";
    var sOutDataSet  = "dsOutRaceAnalysis=dsOutRaceAnalysis";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

