/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var sSubUrl   = "";   //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";   //레포트 파일명
	var sDataSet  = "";   //"UBI 쿼리명, MIP Dataset명"

	var iRow = dsOutRaceAnalysis.currow;

	dsOutRaceAnalysis.Filter("RACE_NO='"+dsOutRaceAnalysis.GetColumn(dsOutRaceAnalysis.currow, "RACE_NO")+"'");
	dsOutRaceAnalysisPrint.CopyF(dsOutRaceAnalysis);
	dsOutRaceAnalysis.UnFilter();

	sRptNm    = "e05070020_race_analysis.jrf";               //레포트 파일명
	sDataSet  = "race_analysis=dsOutRaceAnalysisPrint race_result=dsOutRaceResult";   //"UBI 쿼리명, MIP Dataset명"

    //사진경로 가져오기
    var sImgNm_1 = dsOutRaceAnalysisPrint.GetColumn(0, "IMG_1");
    var sImgNm_2 = dsOutRaceAnalysisPrint.GetColumn(0, "IMG_2");
    var sImgNm_3 = dsOutRaceAnalysisPrint.GetColumn(0, "IMG_3");
    var sImgNm_4 = dsOutRaceAnalysisPrint.GetColumn(0, "IMG_4");

    //if ( fnc_IsNull(sImgNm_1) && fnc_IsNull(sImgNm_2) && fnc_IsNull(sImgNm_3) && fnc_IsNull(sImgNm_4) ) return;

    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName_1 = split(sImgNm_1, "//");
    var sPhysicalFileName_2 = split(sImgNm_2, "//");
    var sPhysicalFileName_3 = split(sImgNm_3, "//");
    var sPhysicalFileName_4 = split(sImgNm_4, "//");

    var ImgUrl_1 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_1[length(sPhysicalFileName_1) - 1];
    var ImgUrl_2 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_2[length(sPhysicalFileName_2) - 1];
    var ImgUrl_3 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_3[length(sPhysicalFileName_3) - 1];
    var ImgUrl_4 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_4[length(sPhysicalFileName_4) - 1];

	var sArg  = " IMG_URL_1="+ImgUrl_1; 
        sArg += " IMG_URL_2="+ImgUrl_2; 
        sArg += " IMG_URL_3="+ImgUrl_3; 
        sArg += " IMG_URL_4="+ImgUrl_4; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	dsOutRaceAnalysis.row = iRow;
}
