/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

	var sFormName   = "";    //호출할 Form ID
	var sInputValue = "";    //입력된 자료가 있다면 Default로 조회 
    var sResponse;           //Popup에서 받을 결과 값

	sResponse = fce_GetSelectFile();

	if ( !fnc_IsNull(sResponse) ) {
		switch(obj.id) {
			case "imgPhoto_1" :   //사진1
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_1_UPDATE", "Y");
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_1"       , sResponse[1]);
				edPhotoNm_1.Text      = sResponse[0] + "\\" + sResponse[1];
				imgPhotoNm_1.ImageID  = edPhotoNm_1.Text;
				imgPhotoNm_1.Text     = "";
				imgPhotoNm_1.FillType = "STRETCH";	
				break;	
			case "imgPhoto_2" :   //사진2
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_2_UPDATE", "Y");
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_2"       , sResponse[1]);
				edPhotoNm_2.Text      = sResponse[0] + "\\" + sResponse[1];
				imgPhotoNm_2.ImageID  = edPhotoNm_2.Text;
				imgPhotoNm_2.Text     = "";
				imgPhotoNm_2.FillType = "STRETCH";	
				break;	
			case "imgPhoto_3" :   //사진3
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_3_UPDATE", "Y");
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_3"       , sResponse[1]);
				edPhotoNm_3.Text      = sResponse[0] + "\\" + sResponse[1];
				imgPhotoNm_3.ImageID  = edPhotoNm_3.Text;
				imgPhotoNm_3.Text     = "";
				imgPhotoNm_3.FillType = "STRETCH";	
				break;	
			case "imgPhoto_4" :   //사진4
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_4_UPDATE", "Y");
				dsOutRaceAnalysis.SetColumn(dsOutRaceAnalysis.currow, "IMG_4"       , sResponse[1]);
				edPhotoNm_4.Text      = sResponse[0] + "\\" + sResponse[1];
				imgPhotoNm_4.ImageID  = edPhotoNm_4.Text;
				imgPhotoNm_4.Text     = "";
				imgPhotoNm_4.FillType = "STRETCH";	
