//row 변경시 
function dsOutRaceAnalysis_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (nRow < 0) return;

	//경주기록 조회
	fcSearchRaceResult(obj,nOldRow,nRow);

	//선택 row가 변경된 경우 해당 경주분석결과의 사진을 보여준다.
	this.imgPhotoNm_1.ImageID = "";
	this.imgPhotoNm_2.ImageID = "";
	this.imgPhotoNm_3.ImageID = "";
	this.imgPhotoNm_4.ImageID = "";
	this.imgPhotoNm_1.Text = "[그림-1]이미지가 등록되지 않음";
	this.imgPhotoNm_2.Text = "[그림-2]이미지가 등록되지 않음";
	this.imgPhotoNm_3.Text = "[그림-3]이미지가 등록되지 않음";
	this.imgPhotoNm_4.Text = "[그림-4]이미지가 등록되지 않음";
	this.edPhotoNm_1.Text  = "";
	this.edPhotoNm_2.Text  = "";
	this.edPhotoNm_3.Text  = "";
	this.edPhotoNm_4.Text  = "";
	var sPhotoNm_1 = dsOutRaceAnalysis.GetColumn(nRow, "IMG_1");
	var sPhotoNm_2 = dsOutRaceAnalysis.GetColumn(nRow, "IMG_2");
	var sPhotoNm_3 = dsOutRaceAnalysis.GetColumn(nRow, "IMG_3");
	var sPhotoNm_4 = dsOutRaceAnalysis.GetColumn(nRow, "IMG_4");

	if ( fnc_IsNull(sPhotoNm_1) && fnc_IsNull(sPhotoNm_2) && fnc_IsNull(sPhotoNm_3) && fnc_IsNull(sPhotoNm_4)) return;
	if ( !fnc_IsNull(sPhotoNm_1) ) this.imgPhotoNm_1.Text = "";
	if ( !fnc_IsNull(sPhotoNm_2) ) this.imgPhotoNm_2.Text = "";
	if ( !fnc_IsNull(sPhotoNm_3) ) this.imgPhotoNm_3.Text = "";
	if ( !fnc_IsNull(sPhotoNm_4) ) this.imgPhotoNm_4.Text = "";

	//var sPhysicalFileName = split(sPtoName, "\\");
	var sPhysicalFileName_1 = split(sPhotoNm_1, "//");
	var sPhysicalFileName_2 = split(sPhotoNm_2, "//");
	var sPhysicalFileName_3 = split(sPhotoNm_3, "//");
	var sPhysicalFileName_4 = split(sPhotoNm_4, "//");

	var url_1 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_1[length(sPhysicalFileName_1) - 1];
	var url_2 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_2[length(sPhysicalFileName_2) - 1];
	var url_3 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_3[length(sPhysicalFileName_3) - 1];
	var url_4 = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName_4[length(sPhysicalFileName_4) - 1];

	this.imgPhotoNm_1.ImageID = url_1;
	this.imgPhotoNm_2.ImageID = url_2;
	this.imgPhotoNm_3.ImageID = url_3;
	this.imgPhotoNm_4.ImageID = url_4;

	imgPhotoNm_1.FillType = "STRETCH";	
	imgPhotoNm_2.FillType = "STRETCH";	
	imgPhotoNm_3.FillType = "STRETCH";	
	imgPhotoNm_4.FillType = "STRETCH";	

}

