{
	fce_GetRace(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSDayOrd.Value, "dsOutSRace");
}

/*-------------------------------------------------------------------------------------------------+
>>  Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcImgDialoge(obj,nX,nY) {
	var sImgNm = "";

	switch(obj.id) {
		case "imgPhotoNm_1" :   //사진1
			sImgNm = dsOutRaceAnalysis.GetColumn(dsOutRaceAnalysis.currow, "IMG_1");
			break;
		case "imgPhotoNm_2" :   //사진2
			sImgNm = dsOutRaceAnalysis.GetColumn(dsOutRaceAnalysis.currow, "IMG_2");
			break;
		case "imgPhotoNm_3" :   //사진3
			sImgNm = dsOutRaceAnalysis.GetColumn(dsOutRaceAnalysis.currow, "IMG_3");
			break;
		case "imgPhotoNm_4" :   //사진4
			sImgNm = dsOutRaceAnalysis.GetColumn(dsOutRaceAnalysis.currow, "IMG_4");
			break;
	}
	if ( fnc_IsNull(sImgNm) ) return;

	var sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
	Dialog("form::E03010013.xml", sArg, 0, 0, true);
}

