+-------------------------------------------------------------------------------------------------*/
function setLocation(){
	var oriBottom = grdOutIntroRunTmPivot.Bottom;
	var extHeight = 0;
	var rowHeight = 19;
	var rowCnt = dsOutRaceResult.GetRowCount();

	var gap = 15;

	if(rowCnt < 6) rowCnt = 6;

	grdOutIntroRunTmPivot.Height = (rowCnt + 1) * rowHeight;

	edtSDL.Top = grdOutIntroRunTmPivot.Bottom;
	edtWIND.Top = grdOutIntroRunTmPivot.Bottom;


	Shape3.Top = edtSDL.Bottom + gap + 8;
	Static3.Top = edtSDL.Bottom + gap;
	Static4.Top = edtSDL.Bottom + gap + 5;

	Grid0.Top = Static3.Bottom;

	Edit0.Top = Static3.Bottom;
	Edit11.Top = Static3.Bottom;

	Edit2.Top = Edit0.Bottom;
	Edit10.Top = Edit0.Bottom;

	Edit3.Top = Edit2.Bottom;
	Edit9.Top = Edit2.Bottom;

	Edit4.Top = Edit3.Bottom;
	Edit8.Top = Edit3.Bottom;

	Edit5.Top = Edit4.Bottom;
	Edit7.Top = Edit4.Bottom;

	Edit6.Top = Edit5.Bottom;
	Edit1.Top = Edit5.Bottom;

	edtSB_SMRY.Top = Edit6.Bottom;

	//Shape1.Height = edtSB_SMRY.Bottom - grdOutIntroRunTmPivot.Top;
}
