/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = 'E05070040';
var sBUTTONLIST   = 'FFFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var bPopup = true;
var bChanged = false;
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {

	//사용자 권한 설정
	sBUTTONLIST   = 'F'+sInpt+'FTFT';
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	if ( IsExistVar("Arg_sRaceDay") && bPopup ) {
		calStrDt.Value = Arg_sRaceDay;
		calEndDt.Value = Arg_sRaceDay;

		bPopup = false;
	} else {       
		calStrDt.Value = fnc_calDate(fnc_currDate(), 90, true);
		calEndDt.Value = fnc_currDate();
	}

	btnDelRow.Enable = false;

	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsOutSdl";
	if (!fnc_FormUnloadCheck(sDataset, sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
	// Return Key Press時 Next Component focus
	if (nChar == 13) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutSdl.ClearData();
    dsOutSdlDtl.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
    	if ( IsExistVar("Arg_sRaceDay") && bChanged ) {
	    Close("Y");	//반환 후 조회
	} else {       
		Close();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    if( !fnc_IsMandatory(calStrDt, "시작일") ) return false;
    if( !fnc_IsMandatory(calEndDt, "종료일") ) return false;

	dsOutSdl.ClearData();
    dsOutSdlDtl.ClearData();

	return true;
}

function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;

	var sServiceName = "e05070040-service:searchSdlAnalysis";
	var sInDataSet   = "";
	var sOutDataSet  = "dsOutSdl=dsOutSdl";

	fce_AddParam("STR_DT" , Trim(calStrDt.Value));
	fce_AddParam("END_DT" , Trim(calEndDt.Value));
	fce_AddParam("POOL_CD", Trim(cmbPoolCd.Value));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "e05070040-service:saveSdlAnalysis";
	var sInDataSet   = "dsOutCsv=dsOutCsv dsOutSdl=dsOutSdl:u";
	var sOutDataSet  = "dsOutSdl=dsOutSdl";

	if (!fnc_IsNull(dsProgBar.GetColumn(0,"FILE_PATH"))) {
		var fileFullPath = dsProgBar.GetColumn(0, "FILE_PATH");
		var fileNm       = dsProgBar.GetColumn(0, "FILE_NM");
		var sPool = "";
		var fileSize = HttpFile.GetFileSizeLocal(dsProgBar.GetColumn(0, "FILE_PATH"));

		if (!fcDataSetCheck(obj, fileNm, fileSize)) return;

		dsOutCsv.ClearData();
		Ext_Csv2Ds(fileFullPath, "dsOutCsv", false, "WIN_NO,CNT,AMOUNT"); // CSV파일의 내용을 DATASET에 입력시킨다. Ext_Csv2Ds(strFileName,strDatasetName,HeadFlag,strColumnId)

		if (!fcSaveCheck()) return;

		if (SubStr(fileNm, 8, 2) == "ex") sPool = "004";	//sPool = "EXA"
		if (SubStr(fileNm, 8, 2) == "ql") sPool = "005";	//sPool = "QNL"
		if (SubStr(fileNm, 8, 2) == "tl") sPool = "006";	//sPool = "TLA"

		fce_AddParam("RACE_DT", "20" + Trim(SubStr(fileNm, 0, 6)));
		fce_AddParam("RACE_NO", Trim(SubStr(fileNm, 6, 2)));
		fce_AddParam("POOL_CD", sPool);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	} else {
		fce_AddParam("RACE_DT", "");
		fce_AddParam("RACE_NO", "");
		fce_AddParam("POOL_CD", "");

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	if ((!fnc_DatasetChangeCheck(dsOutSdl)) && (!fnc_DatasetChangeCheck(dsOutCsv))) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutSdl;
	sMandatoryColID   = "RACE_DT|RACE_NO|POOL_CD";
	sMandatoryColName = "날짜|경주|승식";
	oGrid             = grdListMain;

	if (fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1) {
		return false;
	}

	oDataSet          = dsOutCsv;
	sMandatoryColID   = "WIN_NO|CNT|AMOUNT";
	sMandatoryColName = "투표소|투표수|금액";

	if (fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName) > -1) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if (dsOutSdl.rowcount != 0) {
		dsOutSdl.DeleteRow(dsOutSdl.Row);
	}
}

