/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {
    var sResponse;           //Popup에서 받을 결과 값

	switch(obj.id) {
		case "imgFile" :   //파일
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsProgBar.SetColumn(dsProgBar.currow, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
				dsProgBar.SetColumn(dsProgBar.currow, "FILE_NM"    , sResponse[1]);
			}
			break;	
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if (!fnc_result(ErrorCode, ErrorMSG)) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		dsOutPoolCd.InsertRow(0);
		dsOutPoolCd.SetColumn(0, "CD"   , "");
		dsOutPoolCd.SetColumn(0, "CD_NM", "선택");

        dsOutPoolCd.UnFilter();
        dsOutPoolCd.Filter("CD!='001'&&CD!='002'");

		cmbPoolCd.Index = 0;
	}

	if (sServiceName == "e05070040-service:searchSdlAnalysis") {
        fnc_SetStatus(SNIS_BOA_0003, dsOutSdl.GetRowCount());

		if (dsOutSdl.rowcount > 0) {
			btnDelRow.Enable = true;
		}

		dsOutCsv.ClearData();
		dsProgBar.SetColumn(0, "FILE_NM"   , "");
		dsProgBar.SetColumn(0, "FILE_PATH" , "");
		dsProgBar.SetColumn(0, "FILE_SIZE" , "");
		dsProgBar.SetColumn(0, "PROG_BAR"  , "");
	}

	// 저장 완료 후
	if (sServiceName == "e05070040-service:saveSdlAnalysis") {
        bChanged = true;

		fnc_Message(L_COM_ALT_0011, "N", "저장이");
		fcSearch();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
