function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>
+-------------------------------------------------------------------------------------------------*/
function grdListMain_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var sServiceName = "e05070040-service:searchSdlDtl";
	var sInDataSet   = "";
	var sOutDataSet  = "dsOutSdlDtl=dsOutSdlDtl";

	fce_AddParam("RACE_DT", dsOutSdl.GetColumn(nRow, "RACE_DT"));
	fce_AddParam("RACE_NO", dsOutSdl.GetColumn(nRow, "RACE_NO"));
	fce_AddParam("POOL_CD", dsOutSdl.GetColumn(nRow, "POOL_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>
+-------------------------------------------------------------------------------------------------*/
function fcDataSetCheck(obj, fileNm, fileSize) {
	if (SubStr(fileNm, 11) != "csv") {
		var msg = "파일의 확장자는 csv 로 하셔야 합니다.";
		fnc_Message(msg, "N");
		return false;
	}

	if (SubStr(fileNm, 8, 2) != "ex" && 
		SubStr(fileNm, 8, 2) != "ql" && 
		SubStr(fileNm, 8, 2) != "tl") {
		var msg = "파일이름이 올바르지 않습니다.";
		fnc_Message(msg, "N");
		return false;
	}

	if (Length(fileNm) != 14) {
		var msg = "파일이름이 올바르지 않습니다.";
		fnc_Message(msg, "N");
		return false;
	}

	if (fileSize < 1) {
		var msg = "파일이 비어있습니다.";
		fnc_Message(msg, "N");
		return false;
	}

	return true;
}

