var sBUTTONLIST   = 'FFFTFT';                          //°øÅë±â´ÉÁ¤ÀÇ(¹öÆ°¼øÀ¸·Î T or F)
var sUSERAUTH     = '';                                //»ç¿ëÀÚº° ÇÁ·Î±×·¥ »ç¿ë ±ÇÇÑ
var sFORMCAPTION  = '';                                //FormÀÇ Title Á¤º¸
var sFORMLOCATION = '';                                //FormÀÇ Location Á¤º¸
var sUSERLAVEL    = '';                                //FormÀÇ »ç¿ë  Level
var vREVOBJ       = Array(9);                          //°á°ú°ª Ã³¸®¸¦ À§ÇÑ ¿ÀºêÁ§Æ® 
