/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(cboTMS, "회차")) return false;
}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;
    //fcInit();

	var sServiceName = "e05100040-service:searchDopingRslt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDopingRslt=dsOutDopingRslt";
    dsOutDopingRslt.ClearData();

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutDopingRslt)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	var nRowCnt = dsOutDopingRslt.RowCount();
	for (var i=0; i < nRowCnt; i++) {
		if ( (dsOutDopingRslt.GetColumn(i, "BEFORE_RACE") == 0 && dsOutDopingRslt.GetColumn(i, "AFTER_RACE") == 0)
		  && (dsOutDopingRslt.GetColumn(i, "POSITIVE") == 1 || dsOutDopingRslt.GetColumn(i, "NEGATIVE") == 1) ){
			fnc_Message(SNIS_BOA_E010, "N", dsOutDopingRslt.GetColumn(i, "RACER_NM"));
			return false;
		}
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e05100040-service:saveDopingRslt";
    var sInDataSet   = "dsOutDopingRslt=dsOutDopingRslt:u ";
    var sOutDataSet  = "";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

