//row 변경시 
function dsOutDopingRslt_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutDopingRslt_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) obj.Row = LATEST_ROW_POS;
}

//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//회차 변경시 
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//경정장 DataSet Load 완료 시
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sMbrCd") && bPopup ) {
		cboMbrCd.Value.Value = Arg_sMbrCd;
	} else {       
		cboMbrCd.Value = CURR_USER_MBR_CD;
	}

	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sTms") && bPopup ) {
		cboTms.Value = Arg_sTms;

		bPopup = false;
		fcSearch();
	}
}

function grdPageList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( nCell ) {
		case 3 :	//출주전 선택시		
			dsOutDopingRslt.SetColumn(nRow, "AFTER_RACE", 0);
			break;
		case 4 :	//출주후 선택시
			dsOutDopingRslt.SetColumn(nRow, "BEFORE_RACE", 0);
            break;
		case 5 :	//양성 클릭시
			dsOutDopingRslt.SetColumn(nRow, "NEGATIVE", 0);
			if (dsOutDopingRslt.GetColumn(nRow, "BEFORE_RACE") == 0 && dsOutDopingRslt.GetColumn(nRow, "AFTER_RACE") == 0) {
				fnc_Message(SNIS_BOA_E010, "N", dsOutDopingRslt.GetColumn(nRow, "RACER_NM"));	
			}
            break;
		case 6 :	//음성 클릭시
			dsOutDopingRslt.SetColumn(nRow, "POSITIVE", 0);
			if (dsOutDopingRslt.GetColumn(nRow, "BEFORE_RACE") == 0 && dsOutDopingRslt.GetColumn(nRow, "AFTER_RACE") == 0) {
				fnc_Message(SNIS_BOA_E010, "N", dsOutDopingRslt.GetColumn(nRow, "RACER_NM"));	
			}
            break;
    }
}

function dsOutDopingYn_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var nCnt = dsOutDopingYn.GetColumn(0, "CNT");

	if ( nCnt > 0 )	edtDopingYn.Text = "실시";
	else			edtDopingYn.Text = "미실시";
}
