/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(cboTMS, "회차")) return false;
   if (!fnc_IsMandatory(cboDayOrd, "일차")) return false;
   if (!fnc_IsMandatory(cboRACE_NO, "경주")) return false;
}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;
    //fcInit();

	var sServiceName = "e05100060-service:searchRiskIndex";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRiskIndex=dsOutRiskIndex";
    dsOutRiskIndex.ClearData();

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);
    fce_AddParam("RACE_NO", cboRACE_NO.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRiskIndex)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e05100060-service:saveRiskIndex";
    var sInDataSet   = "dsOutRiskIndex=dsOutRiskIndex:u ";
    var sOutDataSet  = "";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

