//row 변경시 
function dsOutRaceResult_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutRaceResult_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutRaceResult.Row = LATEST_ROW_POS;
}

//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//회차 변경시 
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}

//일차 변경시 
function cboDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRace(cboStndYear.Value, cboMbrCd.Value, cboTms.Value, cboDayOrd.Value, "dsOutSRace");
}

//경정장 DataSet Load 완료 시
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sMbrCd") && bPopup ) {
		cboMbrCd.Value.Value = Arg_sMbrCd;
	} else {       
		cboMbrCd.Value = CURR_USER_MBR_CD;
	}

	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sTms") && bPopup ) {
		cboTms.Value = Arg_sTms;
	} else {       
		//cboTms.Value = CURR_TMS;
		cboTms.index = 0;
	}

	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}

//일차 DataSet Load 완료 시 
function dsOutDayOrd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sDayOrd") && bPopup ) {
		cboDayOrd.Value = Arg_sDayOrd;
	} else {       
		cboDayOrd.Value = CURR_DAY_ORD;
	}

	fce_GetRace(cboStndYear.Value, cboMbrCd.Value, cboTms.Value, cboDayOrd.Value, "dsOutSRace");
}

//경주번호 DataSet Load 완료 시 
function dsOutSRace_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sRaceNo") && bPopup ) {
		cboRACE_NO.Value = Arg_sRaceNo;

		bPopup = false;
		fcSearch();
	}
}

//경주번호 변경시
function cboRACE_NO_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}
