/*-------------------------------------------------------------------------------------------------+
>>  Transaction »ƒ √≥∏Æ «ÿæﬂ «“ ≥ªøÎ!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(ServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// ¡∂»∏ »ƒ
	if ( ServiceName == 'e06010020-service:searchEquipRunStat' ) {
		if(dsOutEquipRunStat.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutEquipRunStat.GetRowCount());
	    }else{
	        fnc_SetStatus(SNIS_BOA_0003,  dsOutEquipRunStat.GetRowCount());

	    }

	}


	//ø¿∑˘∞° æ¯¿Ã ¡§ªÛ √≥∏Æµ«æ˙¥Ÿ∏È...
	fcPostProceS(ServiceName);
}

