function fcGetMinMaxTms(stndYear, mbrCd) {

	var ServiceName = 'e06010020-service:searchMinMaxTms';
    var sInDataSet   = '';
    var sOutDataSet  = 'dsOutTms=dsOutTms';
    fce_AddParam('STND_YEAR', stndYear);
    fce_AddParam('MBR_CD', mbrCd);
    fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, 'fcCallBack', false);
}

function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(dsOutTms.GetRowCount()>0){
		MIN_TMS = dsOutTms.GetColumn(dsOutTms.rowposition, 'MIN_TMS');
		MAX_TMS = dsOutTms.GetColumn(dsOutTms.rowposition, 'MAX_TMS');

		if(MIN_TMS == null){
			alert("µÓ∑œµ» »∏¬˜∞° æ¯Ω¿¥œ¥Ÿ. ");
			return;
		}
	}else{
		alert('»∏¬˜ ¡§∫∏∞° æ¯Ω¿¥œ¥Ÿ.');

	}
}

function dsOutEquipTpeCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboEquipTpeCd.Value = 'M';
}

function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fce_AddCodeRow(dsOutMbrCd, '¿¸√º', '', '');
	cboMbrCd.Value = '';
}


function fcSearchEquipRunStat(stndYear, mbrCd, equipTpeCd, basicDayOrd, minTms, maxTms) {
	if(MIN_TMS == null){
			alert("µÓ∑œµ» »∏¬˜∞° æ¯Ω¿¥œ¥Ÿ. ");
			return;
	}

	var ServiceName = 'e06010020-service:searchEquipRunStat';
    var sInDataSet   = '';
    var sOutDataSet  = 'dsOutEquipRunStat=dsOutEquipRunStat';

    fce_AddParam('STND_YEAR', cboStndYear.Value);
    fce_AddParam('MBR_CD', cboMbrCd.Value);
	fce_AddParam('EQUIP_TPE_CD', cboEquipTpeCd.Value);
	fce_AddParam('BASIC_DAY_ORD', BASIC_DAY_ORD);
	fce_AddParam('MIN_TMS', minTms);
	fce_AddParam('MAX_TMS', maxTms);


	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, 'fcCallBack', false);
}


//Grit Formatª˝º∫ 
function setHeader(minTms, maxTms, basicDayOrd){
	var gridFormat = '';
	gridFormat += " <contents> ";
	gridFormat += " 	<format id='Default'> ";
	gridFormat += " 		<columns> ";
	gridFormat += " 			<col fix='left' width='85'/> ";
	gridFormat += " 			<col fix='left' width='63'/> ";
	gridFormat += " 			<col fix='left' width='62'/> ";


	for(var i=minTms; i<maxTms+1; i++){
		for(var j=0; j<=basicDayOrd; j++){
			gridFormat += " 			<col width='55'/> ";
		}
	}
	gridFormat += " 		</columns> ";
	gridFormat += " 		<head> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='0' color='user21' display='text' font='±º∏≤,9' rowspan='2' text='¿Â∫Òπ¯»£'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='1' color='user21' display='text' font='±º∏≤,9' rowspan='2' text='∞Ê¡§¿Â'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='2' color='user21' display='text' font='±º∏≤,9' rowspan='2' text='√—√‚¡÷ºˆ'/> ";

	var headTmsIndex = 1;
	for(var i=0; i<=(maxTms-minTms); i++){
		gridFormat += " 			<cell align='center' bkcolor='user20' col='"+(headTmsIndex*(basicDayOrd+1))+"' color='user21' colspan='3' display='text' font='±º∏≤,9' text='"+(i+minTms)+"&#32;»∏¬˜'/> ";
		headTmsIndex++;

	}

	var headColumnIndex =3;
	for(var i=minTms; i<maxTms+1; i++){
		for(var j=0; j<=basicDayOrd; j++){
			gridFormat += " 			<cell align='center' bkcolor='user20' col='"+(headColumnIndex++)+"' color='user21' display='text' font='±º∏≤,9' row='1' text='"+j+"¿œ¬˜'/> ";
		}
	}
	gridFormat += " 		</head> ";

	gridFormat += " 		<body> ";
	gridFormat += " 			<cell align='center' col='0' colid='EQUIP_NO' display='text' edit='normal' Mask='@-@@@@-@@@' suppress='1'/> ";
	gridFormat += " 			<cell align='center' col='1' colid='MBR_CD' combocol='CD' combodataset='dsOutMbrCd' combotext='CD_NM' display='combo' edit='combo' suppress='2'/> ";
	gridFormat += " 			<cell col='2' colid='RUN_TOTAL_CNT' display='number' edit='normal'/> ";
	gridFormat += " 			<cell col='3' colid='TMS_1_DAY_0' display='number' edit='normal'/> ";

	var bodyColumnIndex =3;
	for(var i=minTms; i<maxTms+1; i++){
		for(var j=0; j<=basicDayOrd; j++){
			gridFormat += " 			<cell col='"+(bodyColumnIndex++)+"' colid='TMS_"+i+"_DAY_"+j+"' display='number' edit='normal'/> ";
		}
	}
	gridFormat += " 		</body> ";
	gridFormat += " 		<summary> ";
	gridFormat += " 			<cell col='0' display='text' text='∞Ë:'/> ";
	gridFormat += " 			<cell col='1' display='number' expr='dsOutEquipRunStat.GetRowCount()'/> ";
	gridFormat += " 			<cell col='2' display='number' expr='SUM(&apos;RUN_TOTAL_CNT&apos;)'/> ";

	var sumColumnIndex =3;
	for(var i=minTms; i<maxTms+1; i++){
		for(var j=0; j<=basicDayOrd; j++){
			gridFormat += " 			<cell col='"+(sumColumnIndex++)+"' display='number' expr='SUM(&apos;+TMS_"+i+"_DAY_"+j+"&apos;)'/> ";
		}
	}
	gridFormat += " 		</summary> ";
	gridFormat += " 	</format> ";
	gridFormat += " </contents> ";

	grdEquipRunStat.contents = gridFormat;


}

