/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

/*  일괄 등록시에는 변경된 자료는 체크하지 않음.

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEquipReprHis)  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
*/

	// 필수항목체크
	oDataSet          = dsOutEquipReprHis;
	sMandatoryColID   = "REPR_DT|EQUIP_NO|REPR_TPE_CDS";
	sMandatoryColName = "정비일자|모터번호|정비유형";
	oGrid             = grdEquipReprHis;

	var chkRow = fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid);
	if ( chkRow > -1 ) {
		oDataSet.DeleteRow(chkRow);
	    return false;
	}

	return true;
}

function fcSave(obj) {    
    var REPR_STR_DT = "";
    var REPR_END_DT = "";
    var EQUIP_TPE_CD = "";
    var EQUIP_NO = "";
    var REPR_TPE_CD = "";
    var REPR_PARTS = "";
    var REPR_TPE_CD = "";

    var add = false;

    for ( var i = 0; i < dsOutEquip.GetRowCount(); i++) {

		if(dsOutEquip.GetColumn(i, "CHK") == 1){

			dsOutEquipReprHis.AddRow();  
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_DT", CURR_DT);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "MBR_CD", CURR_USER_MBR_CD);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "STND_YEAR", CURR_STNT_YEAR);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "EQUIP_TPE_CD", CURR_EQUIP_TPE);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "EQUIP_NO", dsOutEquip.GetColumn(i, 'EQUIP_NO'));

			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_NM", edRacerNM2.Text);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "MJR_PARTS", edMjrParts.Text);

			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_CDS", edReprTpeCds.Text);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_NMS", edReprTpeNms.Text);

			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_NMS", edReprTpeNms.Text);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_DESC", edReprDesc.Text);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "HOMEPAGE_YN", chkHomepageYn.Value);
			dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "HOMEPAGE_DESC", edHomepageDesc.Text);	

			//일괄 등록시 첫번째 데이터를 가지고 필수 체크함.
			if (!add && !fcSaveCheck()){
				return false;
			}

			add = true;			
		}
    }

    if(!add){
		return;
	}

    var sServiceName = "e06010040-service:saveEquipReprHis";
    var sInDataSet   = "dsOutEquipReprHis=dsOutEquipReprHis:u";
    var sOutDataSet  = "dsOutEquipReprHis=dsOutEquipReprHis";

    var equipTpeCd = CURR_EQUIP_TPE;
    fce_AddParam("REPR_STR_DT", mskReprStrDt.Value);
    fce_AddParam("REPR_END_DT", mskReprEndDt.Value);
    fce_AddParam("EQUIP_TPE_CD", equipTpeCd);
    fce_AddParam("EQUIP_NO", mskEquipNo.Value);
    fce_AddParam("REPR_TPE_CD", cboReprTpeCd.Value);
    fce_AddParam("REPR_PARTS", edParts.Text);
    fce_AddParam("USE_YN", cboUseYn.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

