/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {

    }else if( sServiceName == "e06010040-service:searchEquip" ) {
		initReprHis();
	}else if( sServiceName == "e06010040-service:searchEquipReprHis" ) {
	    if(dsOutEquipReprHis.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutEquipReprHis.GetRowCount());
	    }
	}else if ( sServiceName == "e06010010-service:saveEquipReprHis" ) {
		initReprHis();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

