// 장비 조회  
function fcSearchEquip(){
	var sServiceName = "e06010040-service:searchEquip";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEquip=dsOutEquip";


    var equipTpeCd = CURR_EQUIP_TPE;
    fce_AddParam("REPR_STR_DT", mskReprStrDt.Value);
    fce_AddParam("REPR_END_DT", mskReprEndDt.Value);
    fce_AddParam("EQUIP_TPE_CD", equipTpeCd);
    fce_AddParam("EQUIP_NO", mskEquipNo.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("REPR_PARTS", edParts.Text);
    fce_AddParam("USE_YN", cboUseYn.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

