function btnReprPrint_OnClick(obj)
{
	var sServiceName = "e06010040-service:searchReprPrint";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutReprPrint=dsOutReprPrint";


    fce_AddParam("REPR_STR_DT", mskReprStrDt.Value);
    fce_AddParam("REPR_END_DT", mskReprEndDt.Value);
    fce_AddParam("EQUIP_TPE_CD", "M");
    //fce_AddParam("EQUIP_NO", mskEquipNo.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    //fce_AddParam("REPR_PARTS", edParts.Text);
    //fce_AddParam("USE_YN", cboUseYn.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function dsOutReprPrint_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcPrintReprList();
}

function fcPrintReprList(obj)
{
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019040_repair.jrf";               //레포트 파일명
	var sDataSet  = "dsOutReprPrint=dsOutReprPrint ";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "REPR_STR_DT="+mskReprStrDt.Value;
		sArg += " REPR_END_DT="+mskReprEndDt.Value; 
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

function grdEquip_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 2){
		if(dsOutEquip.GetColumn(nRow, "CHK") == 0){
			dsOutEquip.SetColumn(nRow, "CHK", 1);
		}else{
			dsOutEquip.SetColumn(nRow, "CHK", 0);
		}
	}else if(nCell == 3){
		var equipNo = dsOutEquip.GetColumn(dsOutEquip.rowposition, "EQUIP_NO");
		fcSearchEquipReprHis(equipNo);
	}
}

//모터번호 전체 체크 및 해제.
function grdEquip_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 2){	
		if(grdEquip.GetCellValue(0, 2) == 0){
			grdEquip.SetCellProp("Head",2,"Text",1);
			fnc_SetAllColumnValue(dsOutEquip, "CHK", 1);
		}else{
			grdEquip.SetCellProp("Head",2,"Text",0);
			fnc_SetAllColumnValue(dsOutEquip, "CHK", 0);
		}
	}
}

//모터 정비 이력 클릭시 모터 일괄 정비 이력 세팅.
function grdEquipReprHis_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	calReprDt.Value = dsOutEquipReprHis.GetColumn(nRow, "REPR_DT");
	cbMBR_CD.Value = dsOutEquipReprHis.GetColumn(nRow, "MBR_CD");

	edRacerNM2.Text = dsOutEquipReprHis.GetColumn(nRow, "REPR_NM");
	edMjrParts.Text = dsOutEquipReprHis.GetColumn(nRow, "MJR_PARTS");

	edReprTpeCds.Text = dsOutEquipReprHis.GetColumn(nRow, "REPR_TPE_CDS");
	edReprTpeNms.Text = dsOutEquipReprHis.GetColumn(nRow, "REPR_TPE_NMS");

	edReprTpeNms.Text = dsOutEquipReprHis.GetColumn (nRow, "REPR_TPE_NMS");
	edReprDesc.Text = dsOutEquipReprHis.GetColumn(nRow, "REPR_DESC");
	chkHomepageYn.Value = dsOutEquipReprHis.GetColumn(nRow, "HOMEPAGE_YN");
	edHomepageDesc.Text = dsOutEquipReprHis.GetColumn(nRow, "HOMEPAGE_DESC");

	dsOutGrdReprTpeCd.Reset();

	var reprTpeCds = Split(dsOutEquipReprHis.getColumn(nRow, "REPR_TPE_CDS"),',');
	for(i=0; i<reprTpeCds.length; i++){
		for(j=0; j<dsOutGrdReprTpeCd.getRowCount(); j++){
			if(dsOutGrdReprTpeCd.GetColumn(j, 'CD') == reprTpeCds[i]){
				dsOutGrdReprTpeCd.SetColumn(j, "CHK", 1);
			}
		}
	}
}

//모터 일괄 정비 이력 초기화.
function initReprHis(){
	calReprDt.Value = "";
	cbMBR_CD.Value = "";

	edRacerNM2.Text = "";
	edMjrParts.Text = "";

	edReprTpeCds.Text = "";
	edReprTpeNms.Text = "";

	edReprTpeNms.Text = "";
	edReprDesc.Text = "";
	chkHomepageYn.Value = "1";
	edHomepageDesc.Text = "";

	dsOutGrdReprTpeCd.Reset();
}

function chkHomepageYn_OnClick(obj,strValue)
{
	if(obj.Value == "1"){ //홈페이지 등록
		if(fnc_Message(SNIS_BOA_F010, "Y" )){
			edHomepageDesc.Value = edReprDesc.Value;
		}else{
			chkHomepageYn.value="0";
			return;
		}
	}else{					//홈페이지 등록 취소
		if(fnc_Message(SNIS_BOA_F011, "Y" )){
			edHomepageDesc.Value = "";
		}else{
			chkHomepageYn.value="1";
			return;
		}
	}
}

function edReprDesc_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	if(chkHomepageYn.Value == "1")
		edHomepageDesc.Value = edReprDesc.Value;
}
