var sFormId       = 'E06010080';
var sBUTTONLIST   = 'FFFTFT';                         //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var CURR_DT = fce_GetSysdate("YYYYMMDD");
var CURR_STNT_YEAR = SubStr(CURR_DT,0,4);
var CURR_USER_MBR_CD = gdsRace.GetColumn(gdsRace.rowposition, "MBR_CD");	//현재 User의 경정장 코드 
var CURR_TMS = gdsRace.GetColumn(gdsRace.rowposition, "TMS");			//현재 회차  
var CURR_DAY_ORD = gdsRace.GetColumn(gdsRace.rowposition, "DAY_ORD");		//현재 일차 
var CURR_RACE_DAY = gdsRace.GetColumn(gdsRace.rowposition, "RACE_DAY");		//현재 경주일자  

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
    sBUTTONLIST   = 'F'+sInpt+'FTFT';

	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	if ( sInpt == "T" ) {
        btn_tilt.Enable       = false;
	} else {
        btn_tilt.Enable       = true;
	}

	//http.sync= true;
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutStndYear, toInteger(CURR_STNT_YEAR));

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	//http.sync = false;
	// 화면 초기화
	fcInit();

	//출주검사표 점검내용
	fcSearchReportMemo();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	if ( !fnc_FormUnloadCheck("" , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {	
	dsOutRaceIms.ClearData();
	dsOutRaceOrgan.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboTms, "회차")){
		return false;
   }else if(!fnc_IsMandatory(cboDayOrd, "일차")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e06010080-service:searchRaceIms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRaceIms=dsOutRaceIms dsOutRaceOrgan=dsOutRaceOrgan";       

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutReportMemo)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e06010080-service:saveReportMemo";
    var sInDataSet   = "dsOutReportMemo=dsOutReportMemo ";
    var sOutDataSet  = "";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	//fnc_OpenExcelExport("grdOutIntroRunTmPivot");         //입력 첫 항목으로 Focus
	grdOutRaceOrgan.ExportExcelEx("경주별틸트각내역("+dsOutRaceOrgan.getColumn(dsOutRaceOrgan.row, "RACE_NO")+").xls");
}

/*-------------------------------------------------------------------------------------------------+
>>  출주검사표 점검내용 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchReportMemo(obj) {

    var sServiceName = "e06010080-service:searchReportMemo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutReportMemo=dsOutReportMemo";       

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

