/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06010080-service:searchRaceIms" ) {
		if(dsOutRaceIms.GetRowCount()>0){

		}
	    if(dsOutRaceOrgan.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutRaceOrgan.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }

	}else if(sServiceName == "e06010080-service:searchRaceOrgan"){

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

