//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}


//회차 변경시 
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}


//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboTms.Value = CURR_TMS;
	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}

//일차 DataSetLoad 완료 시 
function dsOutDayOrd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(CURR_RACE_DAY>CURR_DT){
		cboDayOrd.Value = CURR_DAY_ORD;
    }
    fcSearch();
}


//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//일차 변경시 
function cboDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//경주 편성정보및 틸트각 정보 조회 
function getRaceOragn(){
	var sServiceName = "e06010080-service:searchRaceOrgan";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRaceOrgan=dsOutRaceOrgan";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);
    fce_AddParam("RACE_NO", dsOutRaceIms.GetColumn(dsOutRaceIms.rowposition, 'RACE_NO'));

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);  
}

//경주 Row 변경시 
function dsOutRaceIms_OnRowPosChanged(obj,nOldRow,nRow)
{
	 getRaceOragn();
}

//경주 onload 
function dsOutRaceIms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	getRaceOragn();
}

function btn_tilt_OnClick(obj)
{
 	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019080_race_tilt_report.jrf";               //레포트 파일명
	var sDataSet  = "dsOutRaceOrgan=dsOutRaceOrgan";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "RACE_DAY="+dsOutRaceIms.getColumn(0, "RACE_DAY");
		sArg += " TMS="+cboTms.Value; 
		sArg += " DAY_ORD="+cboDayOrd.Value; 	
		sArg += " RACE_NO="+dsOutRaceOrgan.getColumn(0, "RACE_NO"); 	
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	//프린트 여부 저장 
	dsOutRaceIms.setColumn(dsOutRaceIms.rowposition, 'TILT_PRINT_YN', 'Y');
	saveTiltPrint();

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}

function saveTiltPrint() {
	var sServiceName = "e06010080-service:saveTiltPrint";
    var sInDataSet   = "dsOutRaceIms=dsOutRaceIms:u";   
    var sOutDataSet  = "";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//출주전 검사표 출력 
function btnInspReport_OnClick(obj)
{
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019080_race_ims_report.jrf";               //레포트 파일명
	var sDataSet  = "dsOutRaceOrgan=dsOutRaceOrgan "
	              + "dsOutReportMemo=dsOutReportMemo";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "RACE_DAY="+dsOutRaceIms.getColumn(0, "RACE_DAY");
		sArg += " TMS="+cboTms.Value; 
		sArg += " DAY_ORD="+cboDayOrd.Value; 	
		sArg += " RACE_NO="+dsOutRaceOrgan.getColumn(0, "RACE_NO"); 	
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	//프린트 여부 저장 
	dsOutRaceIms.setColumn(dsOutRaceIms.rowposition, 'BEFORE_INSP_PRINT_YN', 'Y');
	saveRunInspPrint();

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}


//출주 직전검사표 
