{
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019080_race_organ_report.jrf";               //레포트 파일명
	var sDataSet  = "dsOutRaceOrgan=dsOutRaceOrgan "
	              + "dsOutReportMemo=dsOutReportMemo";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "RACE_DAY="+dsOutRaceIms.getColumn(0, "RACE_DAY");
		sArg += " TMS="+cboTms.Value; 
		sArg += " DAY_ORD="+cboDayOrd.Value; 	
		sArg += " RACE_NO="+dsOutRaceOrgan.getColumn(0, "RACE_NO"); 	
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	//프린트 여부 저장 
	dsOutRaceIms.setColumn(dsOutRaceIms.rowposition, 'INSP_PRINT_YN', 'Y');
