	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboTms, "회차")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e06010130-service:searchExpendableChg";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutExpendableChg=dsOutExpendableChg";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchDetail(obj, nRow) 
{
    var sServiceName = "e06010130-service:searchExpendableChgDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutExpendableChgDetail=dsOutExpendableChgDetail";

    fce_AddParam("STND_YEAR", dsOutExpendableChg.GetColumn(nRow, "STND_YEAR"));
    fce_AddParam("MBR_CD", dsOutExpendableChg.GetColumn(nRow,"MBR_CD"));
	fce_AddParam("MOT_NO", dsOutExpendableChg.GetColumn(nRow,"MOT_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

